import{_ as s,c as a,o as e,b as n}from"./chunks/framework.B9NywAPp.js";const l=JSON.parse('{"title":"Compile-Time Flags","description":"","frontmatter":{"outline":"deep"},"headers":[{"level":2,"title":"__VUE_OPTIONS_API__","slug":"VUE_OPTIONS_API","link":"#VUE_OPTIONS_API","children":[]},{"level":2,"title":"__VUE_PROD_DEVTOOLS__","slug":"VUE_PROD_DEVTOOLS","link":"#VUE_PROD_DEVTOOLS","children":[]},{"level":2,"title":"__VUE_PROD_HYDRATION_MISMATCH_DETAILS__","slug":"VUE_PROD_HYDRATION_MISMATCH_DETAILS","link":"#VUE_PROD_HYDRATION_MISMATCH_DETAILS","children":[]},{"level":2,"title":"Configuration Guides","slug":"configuration-guides","link":"#configuration-guides","children":[{"level":3,"title":"Vite","slug":"vite","link":"#vite","children":[]},{"level":3,"title":"vue-cli","slug":"vue-cli","link":"#vue-cli","children":[]},{"level":3,"title":"webpack","slug":"webpack","link":"#webpack","children":[]},{"level":3,"title":"Rollup","slug":"rollup","link":"#rollup","children":[]}]}],"relativePath":"api/compile-time-flags.md","filePath":"api/compile-time-flags.md"}'),o={name:"api/compile-time-flags.md"},p=[n('<h1 id="compile-time-flags" tabindex="-1">Compile-Time Flags <a class="header-anchor" href="#compile-time-flags" aria-label="Permalink to &quot;Compile-Time Flags {#compile-time-flags}&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Compile-time flags only apply when using the <code>esm-bundler</code> build of Vue (i.e. <code>vue/dist/vue.esm-bundler.js</code>).</p></div><p>When using Vue with a build step, it is possible to configure a number of compile-time flags to enable / disable certain features. The benefit of using compile-time flags is that features disabled this way can be removed from the final bundle via tree-shaking.</p><p>Vue will work even if these flags are not explicitly configured. However, it is recommended to always configure them so that the relevant features can be properly removed when possible.</p><p>See <a href="#configuration-guides">Configuration Guides</a> on how to configure them depending on your build tool.</p><h2 id="VUE_OPTIONS_API" tabindex="-1"><code>__VUE_OPTIONS_API__</code> <a class="header-anchor" href="#VUE_OPTIONS_API" aria-label="Permalink to &quot;`__VUE_OPTIONS_API__` {#VUE_OPTIONS_API}&quot;">​</a></h2><ul><li><p><strong>Default:</strong> <code>true</code></p><p>Enable / disable Options API support. Disabling this will result in smaller bundles, but may affect compatibility with 3rd party libraries if they rely on Options API.</p></li></ul><h2 id="VUE_PROD_DEVTOOLS" tabindex="-1"><code>__VUE_PROD_DEVTOOLS__</code> <a class="header-anchor" href="#VUE_PROD_DEVTOOLS" aria-label="Permalink to &quot;`__VUE_PROD_DEVTOOLS__` {#VUE_PROD_DEVTOOLS}&quot;">​</a></h2><ul><li><p><strong>Default:</strong> <code>false</code></p><p>Enable / disable devtools support in production builds. This will result in more code included in the bundle, so it is recommended to only enable this for debugging purposes.</p></li></ul><h2 id="VUE_PROD_HYDRATION_MISMATCH_DETAILS" tabindex="-1"><code>__VUE_PROD_HYDRATION_MISMATCH_DETAILS__</code> <sup class="vt-badge" data-text="3.4+"></sup> <a class="header-anchor" href="#VUE_PROD_HYDRATION_MISMATCH_DETAILS" aria-label="Permalink to &quot;`__VUE_PROD_HYDRATION_MISMATCH_DETAILS__` &lt;sup class=&quot;vt-badge&quot; data-text=&quot;3.4+&quot; /&gt; {#VUE_PROD_HYDRATION_MISMATCH_DETAILS}&quot;">​</a></h2><ul><li><p><strong>Default:</strong> <code>false</code></p><p>Enable/disable detailed warnings for hydration mismatches in production builds. This will result in more code included in the bundle, so it is recommended to only enable this for debugging purposes.</p></li></ul><h2 id="configuration-guides" tabindex="-1">Configuration Guides <a class="header-anchor" href="#configuration-guides" aria-label="Permalink to &quot;Configuration Guides {#configuration-guides}&quot;">​</a></h2><h3 id="vite" tabindex="-1">Vite <a class="header-anchor" href="#vite" aria-label="Permalink to &quot;Vite {#vite}&quot;">​</a></h3><p><code>@vitejs/plugin-vue</code> automatically provides default values for these flags. To change the default values, use Vite&#39;s <a href="https://vitejs.dev/config/shared-options.html#define" target="_blank" rel="noreferrer"><code>define</code> config option</a>:</p><div class="language-js"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;">// vite.config.js</span></span>\n<span class="line"><span style="color:#F97583;">import</span><span style="color:#E1E4E8;"> { defineConfig } </span><span style="color:#F97583;">from</span><span style="color:#9ECBFF;"> &#39;vite&#39;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#F97583;">export</span><span style="color:#F97583;"> default</span><span style="color:#B392F0;"> defineConfig</span><span style="color:#E1E4E8;">({</span></span>\n<span class="line"><span style="color:#E1E4E8;">  define: {</span></span>\n<span class="line"><span style="color:#6A737D;">    // enable hydration mismatch details in production build</span></span>\n<span class="line"><span style="color:#E1E4E8;">    __VUE_PROD_HYDRATION_MISMATCH_DETAILS__: </span><span style="color:#9ECBFF;">&#39;true&#39;</span></span>\n<span class="line"><span style="color:#E1E4E8;">  }</span></span>\n<span class="line"><span style="color:#E1E4E8;">})</span></span></code></pre></div><h3 id="vue-cli" tabindex="-1">vue-cli <a class="header-anchor" href="#vue-cli" aria-label="Permalink to &quot;vue-cli {#vue-cli}&quot;">​</a></h3><p><code>@vue/cli-service</code> automatically provides default values for some of these flags. To configure /change the values:</p><div class="language-js"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;">// vue.config.js</span></span>\n<span class="line"><span style="color:#79B8FF;">module</span><span style="color:#E1E4E8;">.</span><span style="color:#79B8FF;">exports</span><span style="color:#F97583;"> =</span><span style="color:#E1E4E8;"> {</span></span>\n<span class="line"><span style="color:#B392F0;">  chainWebpack</span><span style="color:#E1E4E8;">: (</span><span style="color:#FFAB70;">config</span><span style="color:#E1E4E8;">) </span><span style="color:#F97583;">=&gt;</span><span style="color:#E1E4E8;"> {</span></span>\n<span class="line"><span style="color:#E1E4E8;">    config.</span><span style="color:#B392F0;">plugin</span><span style="color:#E1E4E8;">(</span><span style="color:#9ECBFF;">&#39;define&#39;</span><span style="color:#E1E4E8;">).</span><span style="color:#B392F0;">tap</span><span style="color:#E1E4E8;">((</span><span style="color:#FFAB70;">definitions</span><span style="color:#E1E4E8;">) </span><span style="color:#F97583;">=&gt;</span><span style="color:#E1E4E8;"> {</span></span>\n<span class="line"><span style="color:#E1E4E8;">      Object.</span><span style="color:#B392F0;">assign</span><span style="color:#E1E4E8;">(definitions[</span><span style="color:#79B8FF;">0</span><span style="color:#E1E4E8;">], {</span></span>\n<span class="line"><span style="color:#E1E4E8;">        __VUE_OPTIONS_API__: </span><span style="color:#9ECBFF;">&#39;true&#39;</span><span style="color:#E1E4E8;">,</span></span>\n<span class="line"><span style="color:#E1E4E8;">        __VUE_PROD_DEVTOOLS__: </span><span style="color:#9ECBFF;">&#39;false&#39;</span><span style="color:#E1E4E8;">,</span></span>\n<span class="line"><span style="color:#E1E4E8;">        __VUE_PROD_HYDRATION_MISMATCH_DETAILS__: </span><span style="color:#9ECBFF;">&#39;false&#39;</span></span>\n<span class="line"><span style="color:#E1E4E8;">      })</span></span>\n<span class="line"><span style="color:#F97583;">      return</span><span style="color:#E1E4E8;"> definitions</span></span>\n<span class="line"><span style="color:#E1E4E8;">    })</span></span>\n<span class="line"><span style="color:#E1E4E8;">  }</span></span>\n<span class="line"><span style="color:#E1E4E8;">}</span></span></code></pre></div><h3 id="webpack" tabindex="-1">webpack <a class="header-anchor" href="#webpack" aria-label="Permalink to &quot;webpack {#webpack}&quot;">​</a></h3><p>Flags should be defined using webpack&#39;s <a href="https://webpack.js.org/plugins/define-plugin/" target="_blank" rel="noreferrer">DefinePlugin</a>:</p><div class="language-js"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;">// webpack.config.js</span></span>\n<span class="line"><span style="color:#79B8FF;">module</span><span style="color:#E1E4E8;">.</span><span style="color:#79B8FF;">exports</span><span style="color:#F97583;"> =</span><span style="color:#E1E4E8;"> {</span></span>\n<span class="line"><span style="color:#6A737D;">  // ...</span></span>\n<span class="line"><span style="color:#E1E4E8;">  plugins: [</span></span>\n<span class="line"><span style="color:#F97583;">    new</span><span style="color:#E1E4E8;"> webpack.</span><span style="color:#B392F0;">DefinePlugin</span><span style="color:#E1E4E8;">({</span></span>\n<span class="line"><span style="color:#E1E4E8;">      __VUE_OPTIONS_API__: </span><span style="color:#9ECBFF;">&#39;true&#39;</span><span style="color:#E1E4E8;">,</span></span>\n<span class="line"><span style="color:#E1E4E8;">      __VUE_PROD_DEVTOOLS__: </span><span style="color:#9ECBFF;">&#39;false&#39;</span><span style="color:#E1E4E8;">,</span></span>\n<span class="line"><span style="color:#E1E4E8;">      __VUE_PROD_HYDRATION_MISMATCH_DETAILS__: </span><span style="color:#9ECBFF;">&#39;false&#39;</span></span>\n<span class="line"><span style="color:#E1E4E8;">    })</span></span>\n<span class="line"><span style="color:#E1E4E8;">  ]</span></span>\n<span class="line"><span style="color:#E1E4E8;">}</span></span></code></pre></div><h3 id="rollup" tabindex="-1">Rollup <a class="header-anchor" href="#rollup" aria-label="Permalink to &quot;Rollup {#rollup}&quot;">​</a></h3><p>Flags should be defined using <a href="https://github.com/rollup/plugins/tree/master/packages/replace" target="_blank" rel="noreferrer">@rollup/plugin-replace</a>:</p><div class="language-js"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki github-dark vp-code" tabindex="0"><code><span class="line"><span style="color:#6A737D;">// rollup.config.js</span></span>\n<span class="line"><span style="color:#F97583;">import</span><span style="color:#E1E4E8;"> replace </span><span style="color:#F97583;">from</span><span style="color:#9ECBFF;"> &#39;@rollup/plugin-replace&#39;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#F97583;">export</span><span style="color:#F97583;"> default</span><span style="color:#E1E4E8;"> {</span></span>\n<span class="line"><span style="color:#E1E4E8;">  plugins: [</span></span>\n<span class="line"><span style="color:#B392F0;">    replace</span><span style="color:#E1E4E8;">({</span></span>\n<span class="line"><span style="color:#E1E4E8;">      __VUE_OPTIONS_API__: </span><span style="color:#9ECBFF;">&#39;true&#39;</span><span style="color:#E1E4E8;">,</span></span>\n<span class="line"><span style="color:#E1E4E8;">      __VUE_PROD_DEVTOOLS__: </span><span style="color:#9ECBFF;">&#39;false&#39;</span><span style="color:#E1E4E8;">,</span></span>\n<span class="line"><span style="color:#E1E4E8;">      __VUE_PROD_HYDRATION_MISMATCH_DETAILS__: </span><span style="color:#9ECBFF;">&#39;false&#39;</span></span>\n<span class="line"><span style="color:#E1E4E8;">    })</span></span>\n<span class="line"><span style="color:#E1E4E8;">  ]</span></span>\n<span class="line"><span style="color:#E1E4E8;">}</span></span></code></pre></div>',24)];const t=s(o,[["render",function(s,n,l,o,t,i){return e(),a("div",null,p)}]]);export{l as __pageData,t as default};
